import React, { useState } from 'react';

function TaskItem({ task, updateTask, deleteTask, toggleComplete }) {
  const [isEditing, setIsEditing] = useState(false);
  const [title, setTitle] = useState(task.title);
  const [description, setDescription] = useState(task.description);

  const handleUpdate = () => {
    if (title.trim() && description.trim()) {
      updateTask({ ...task, title, description });
      setIsEditing(false);
    }
  };

  return (
    <div className={`task-item ${task.complete ? 'complete' : ''}`}>
      {isEditing ? (
        <div className="task-edit-form">
          <input
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Edit title"
          />
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            placeholder="Edit description"
          />
          <button onClick={handleUpdate}>Save</button>
          <button onClick={() => setIsEditing(false)}>Cancel</button>
        </div>
      ) : (
        <div className="task-content">
          <input 
            type="checkbox" 
            checked={task.complete} 
            onChange={() => toggleComplete(task.id)} 
          />
          <div className="task-details">
            <h3 className={task.complete ? 'task-title-complete' : ''}>{task.title}</h3>
            <p className={task.complete ? 'task-desc-complete' : ''}>{task.description}</p>
          </div>
          <button onClick={() => setIsEditing(true)}>Edit</button>
        </div>
      )}
      <button onClick={() => deleteTask(task.id)}>Delete</button>
    </div>
  );
}

export default TaskItem;
