import React, { useState } from 'react';
import TaskForm from '../components/TaskForm';

// Page d'accueil avec le formulaire d'ajout de t창che
function HomePage() {
  const [tasks, setTasks] = useState([]);

  // Fonction pour ajouter une nouvelle t창che
  const addTask = (task) => {
    setTasks([...tasks, task]);
  };

  return (
    <div className="home-page">
      <header>
        <h1>Todo App</h1>
        <p>Manage your daily tasks easily with this simple Todo App.</p>
      </header>
      <TaskForm addTask={addTask} />
    </div>
  );
}

export default HomePage;
