import React, { useState } from 'react';
import TaskForm from '../components/TaskForm';
import TaskList from '../components/TaskList';

// Page d'accueil avec la liste des tâches et le formulaire d'ajout de tâche
function HomePage() {
  const [tasks, setTasks] = useState([]);

  // Fonction pour ajouter une nouvelle tâche
  const addTask = (task) => {
    const newTask = { ...task, id: Date.now(), completed: false };
    setTasks([...tasks, newTask]);
  };

  // Fonction pour marquer une tâche comme complète/incomplète
  const toggleComplete = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  // Fonction pour éditer une tâche
  const editTask = (id) => {
    const title = prompt('Edit the task title:');
    const description = prompt('Edit the task description:');
    if (title) {
      setTasks(
        tasks.map((task) =>
          task.id === id ? { ...task, title, description } : task
        )
      );
    }
  };

  // Fonction pour supprimer une tâche
  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  return (
    <div className="home-page">
      <div className="task-form-section">
        <header>
          <h1>Todo App</h1>
          <p>Manage your daily tasks easily with this simple Todo App.</p>
        </header>
        <TaskForm addTask={addTask} />
      </div>
      <div className="task-list-section">
        <TaskList
          tasks={tasks}
          toggleComplete={toggleComplete}
          editTask={editTask}
          deleteTask={deleteTask}
        />
      </div>
    </div>
  );
}

export default HomePage;
